// src/components/Checkout.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import QRCode from 'qrcode.react';

const Checkout = () => {
  const [invoice, setInvoice] = useState(null);
  const [paymentConfirmed, setPaymentConfirmed] = useState(false);
  const [orderNumber, setOrderNumber] = useState('');
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const total = queryParams.get('total');

  const handleCheckout = async () => {
    try {
      const response = await axios.post('http://localhost:5000/create-invoice', {
        price: total,
        currency: 'USD', // or the currency you want to use
      });
      setInvoice(response.data);
    } catch (error) {
      console.error('Error creating invoice:', error);
    }
  };

  useEffect(() => {
    handleCheckout();
  }, [total]);

  // Mock function to simulate payment confirmation (should be replaced with actual webhook handling logic)
  useEffect(() => {
    const simulatePaymentConfirmation = () => {
      setTimeout(() => {
        setPaymentConfirmed(true);
        setOrderNumber('12345'); // Generate or fetch the actual order number
      }, 10000); // Simulate payment confirmation after 10 seconds
    };

    simulatePaymentConfirmation();
  }, []);

  return (
    <div>
      <h2>Checkout</h2>
      {!paymentConfirmed && invoice && (
        <div>
          <p>Invoice created! Scan the QR code or use the link below to pay:</p>
          <QRCode value={invoice.checkoutLink} size={256} />
          <p>
            <a href={invoice.checkoutLink} target="_blank" rel="noopener noreferrer">
              {invoice.checkoutLink}
            </a>
          </p>
        </div>
      )}
      {paymentConfirmed && (
        <div>
          <h3>Payment Confirmed!</h3>
          <p>Your order number is: {orderNumber}</p>
        </div>
      )}
    </div>
  );
};

export default Checkout;
