import React from 'react';
import { useCart } from '../contexts/CartContext';
import Modal from './Modal';
import './ProductList.css';

const products = [
  { id: 1, name: 'Product 1', price: 100.00, image: 'https://via.placeholder.com/150' },
  { id: 2, name: 'Product 2', price: 150.00, image: 'https://via.placeholder.com/150' },
  { id: 3, name: 'Product 3', price: 50.00, image: 'https://via.placeholder.com/150' },
  { id: 4, name: 'Product 4', price: 25.00, image: 'https://via.placeholder.com/150' },
  { id: 5, name: 'Product 5', price: 125.00, image: 'https://via.placeholder.com/150' },
  { id: 6, name: 'Product 6', price: 99.99, image: 'https://via.placeholder.com/150' },
];

const ProductList = ({ searchQuery }) => {
  const { addToCart, modal, closeModal } = useCart();

  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="row">
      {filteredProducts.map((product) => (
        <div className="col-md-4 mb-4" key={product.id}>
          <div className="card h-100">
            <img src={product.image} className="card-img-top" alt={product.name} />
            <div className="card-body">
              <h5 className="card-title">{product.name}</h5>
              <p className="card-text">${product.price}</p>
              <button className="btn btn-primary" onClick={() => addToCart(product)}>Add to Cart</button>
            </div>
          </div>
        </div>
      ))}
      <Modal show={modal.show} message={modal.message} onClose={closeModal} />
    </div>
  );
};

export default ProductList;
