// src/components/Checkout.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

const Checkout = () => {
  const [invoiceUrl, setInvoiceUrl] = useState('');
  const [paymentConfirmed, setPaymentConfirmed] = useState(false);
  const [orderNumber, setOrderNumber] = useState('');
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const total = queryParams.get('total');

  const handleCheckout = async () => {
    try {
      const response = await axios.post('http://localhost:5000/create-invoice', {
        price: total,
        currency: 'USD', // or the currency you want to use
      });
      setInvoiceUrl(response.data.checkoutLink);
    } catch (error) {
      console.error('Error creating invoice:', error);
    }
  };

  useEffect(() => {
    handleCheckout();
  }, [total]);

  useEffect(() => {
    const checkPaymentStatus = async () => {
      try {
        // Replace with your logic to check the payment status (e.g., polling or listening to WebSocket)
        // Here, we simulate the payment confirmation after 10 seconds for demo purposes
        setTimeout(() => {
          setPaymentConfirmed(true);
          setOrderNumber('12345'); // Generate or fetch the actual order number
        }, 10000);
      } catch (error) {
        console.error('Error checking payment status:', error);
      }
    };

    checkPaymentStatus();
  }, [invoiceUrl]);

  return (
    <div>
      <h2>Checkout</h2>
      {!paymentConfirmed && invoiceUrl && (
        <div>
          <p>Invoice created! Pay using the link below:</p>
          <a href={invoiceUrl} target="_blank" rel="noopener noreferrer">
            {invoiceUrl}
          </a>
        </div>
      )}
      {paymentConfirmed && (
        <div>
          <h3>Payment Confirmed!</h3>
          <p>Your order number is: {orderNumber}</p>
        </div>
      )}
    </div>
  );
};

export default Checkout;
