// src/components/Checkout.js
import React, { useState } from 'react';
import axios from 'axios';

const Checkout = () => {
  const [price, setPrice] = useState('');
  const [invoiceUrl, setInvoiceUrl] = useState('');

  const handleCheckout = async () => {
    try {
      const response = await axios.post('/create-invoice', {
        price,
        currency: 'USD', // or the currency you want to use
      });
      setInvoiceUrl(response.data.checkoutLink);
    } catch (error) {
      console.error('Error creating invoice:', error);
    }
  };

  return (
    <div>
      <h2>Checkout</h2>
      <input
        type="number"
        value={price}
        onChange={(e) => setPrice(e.target.value)}
        placeholder="Enter amount"
      />
      <button onClick={handleCheckout}>Pay with Bitcoin</button>
      {invoiceUrl && (
        <div>
          <p>Invoice created! Pay using the link below:</p>
          <a href={invoiceUrl} target="_blank" rel="noopener noreferrer">
            {invoiceUrl}
          </a>
        </div>
      )}
    </div>
  );
};

export default Checkout;
