// src/components/Checkout.js
import React, { useContext, useEffect, useState } from 'react';
import { CartContext } from '../contexts/CartContext';
import axios from 'axios';
import QRCode from 'qrcode.react';

const CheckoutPage = () => {
  const { cartItems, totalAmount } = useContext(CartContext);
  const [invoiceUrl, setInvoiceUrl] = useState(null);
  const [qrCode, setQrCode] = useState(null);
  const [isPaid, setIsPaid] = useState(false);

  const handleCheckout = async () => {
    try {
      const response = await axios.post('/create-invoice', {
        price: totalAmount.toFixed(2),
        currency: 'USD',
      });
      setInvoiceUrl(response.data.url);
      setQrCode(response.data.qrCode);
    } catch (error) {
      console.error('Error creating invoice:', error);
    }
  };

  useEffect(() => {
    const socket = io();
    socket.on('paymentReceived', (data) => {
      if (data.status === 'confirmed') {
        setIsPaid(true);
      }
    });
    return () => socket.disconnect();
  }, []);

  return (
    <div className="checkout-container">
      <h1>Checkout</h1>
      <h2>Total: ${totalAmount.toFixed(2)}</h2>
      <button onClick={handleCheckout}>Proceed to Checkout</button>
      {invoiceUrl && (
        <>
          {!isPaid && (
            <>
              <p>Invoice created! Pay using the link below:</p>
              <a href={invoiceUrl} target="_blank" rel="noopener noreferrer">{invoiceUrl}</a>
              <p>Or scan the QR code below:</p>
              <QRCode value={qrCode} />
            </>
          )}
          {isPaid && (
            <p>Payment confirmed! Thank you for your purchase. Your order number is: {Math.random().toString(36).substr(2, 9)}</p>
          )}
        </>
      )}
    </div>
  );
};

export default CheckoutPage;
