import React, { useState } from 'react';
import { useCart } from '../contexts/CartContext';
import axios from 'axios';
import QRCode from 'qrcode.react';

const CheckoutPage = () => {
  const { total, cartItems } = useCart();
  const [invoiceUrl, setInvoiceUrl] = useState(null);
  const [invoicePaid, setInvoicePaid] = useState(false);

  const handleCheckout = async () => {
    try {
      const response = await axios.post('/checkout', { price: total, currency: 'USD' });
      setInvoiceUrl(response.data.url);
      // Assuming your server emits an event when the invoice is paid
      const socket = io();
      socket.on('invoicePaid', () => {
        setInvoicePaid(true);
      });
    } catch (error) {
      console.error('Error creating invoice:', error);
    }
  };

  return (
    <div className="checkout-container">
      <h1>Checkout</h1>
      <p>Total: ${total.toFixed(2)}</p>
      {!invoiceUrl && <button onClick={handleCheckout}>Proceed to Checkout</button>}
      {invoiceUrl && !invoicePaid && (
        <>
          <p>Invoice created! Pay using the link below:</p>
          <a href={invoiceUrl} target="_blank" rel="noopener noreferrer">
            {invoiceUrl}
          </a>
          <p>Or scan the QR code below:</p>
          <QRCode value={invoiceUrl} />
        </>
      )}
      {invoicePaid && <p>Payment confirmed! Thank you for your purchase.</p>}
    </div>
  );
};

export default CheckoutPage;
