// src/components/ProductList.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../contexts/CartContext';
import Modal from './Modal';
import './ProductList.css';

const products = [
  { id: 1, name: 'Product 1', price: 100.00, category: 'Electronics', rating: 4.5, image: 'https://via.placeholder.com/250' },
  { id: 2, name: 'Product 2', price: 150.00, category: 'Clothing', rating: 4.0, image: 'https://via.placeholder.com/250' },
  { id: 3, name: 'Product 3', price: 200.00, category: 'Home', rating: 3.5, image: 'https://via.placeholder.com/250' },
  { id: 4, name: 'Product 4', price: 250.00, category: 'Electronics', rating: 5.0, image: 'https://via.placeholder.com/250' },
  { id: 5, name: 'Product 5', price: 300.00, category: 'Clothing', rating: 2.5, image: 'https://via.placeholder.com/250' },
  { id: 6, name: 'Product 6', price: 350.00, category: 'Home', rating: 3.0, image: 'https://via.placeholder.com/250' },
];

const ProductList = ({ searchQuery }) => {
  const { addToCart, modal, closeModal } = useCart();
  const [filter, setFilter] = useState({ category: '', minPrice: '', maxPrice: '', rating: '' });
  const [sort, setSort] = useState('');

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilter({ ...filter, [name]: value });
  };

  const handleSortChange = (e) => {
    setSort(e.target.value);
  };

  const filteredProducts = products
    .filter(product => 
      (filter.category === '' || product.category === filter.category) &&
      (filter.minPrice === '' || product.price >= parseFloat(filter.minPrice)) &&
      (filter.maxPrice === '' || product.price <= parseFloat(filter.maxPrice)) &&
      (filter.rating === '' || product.rating >= parseFloat(filter.rating))
    )
    .sort((a, b) => {
      if (sort === 'priceAsc') return a.price - b.price;
      if (sort === 'priceDesc') return b.price - a.price;
      if (sort === 'rating') return b.rating - a.rating;
      if (sort === 'name') return a.name.localeCompare(b.name);
      return 0;
    })
    .filter(product => product.name.toLowerCase().includes(searchQuery.toLowerCase()));

  return (
    <div>
      <div className="filter-sort-container">
        <div className="filters">
          <h5>Filter By</h5>
          <div>
            <label>Category</label>
            <select name="category" value={filter.category} onChange={handleFilterChange}>
              <option value="">All</option>
              <option value="Electronics">Electronics</option>
              <option value="Clothing">Clothing</option>
              <option value="Home">Home</option>
            </select>
          </div>
          <div>
            <label>Min Price</label>
            <input type="number" name="minPrice" value={filter.minPrice} onChange={handleFilterChange} />
          </div>
          <div>
            <label>Max Price</label>
            <input type="number" name="maxPrice" value={filter.maxPrice} onChange={handleFilterChange} />
          </div>
          <div>
            <label>Rating</label>
            <select name="rating" value={filter.rating} onChange={handleFilterChange}>
              <option value="">All</option>
              <option value="1">1 & up</option>
              <option value="2">2 & up</option>
              <option value="3">3 & up</option>
              <option value="4">4 & up</option>
              <option value="5">5</option>
            </select>
          </div>
        </div>
        <div className="sort">
          <h5>Sort By</h5>
          <select value={sort} onChange={handleSortChange}>
            <option value="">None</option>
            <option value="priceAsc">Price: Low to High</option>
            <option value="priceDesc">Price: High to Low</option>
            <option value="rating">Rating</option>
            <option value="name">Name</option>
          </select>
        </div>
      </div>
      <div className="row justify-content-center">
        {filteredProducts.map((product) => (
          <div className="col-md-4 mb-4 d-flex justify-content-center" key={product.id}>
            <div className="product-card">
              <img src={product.image} className="card-img-top" alt={product.name} />
              <div className="card-body">
                <h5 className="product-card-title">
                  <Link to={`/product/${product.id}`}>{product.name}</Link>
                </h5>
                <p className="product-card-price">${product.price.toFixed(2)}</p>
                <button className="btn btn-primary" onClick={() => addToCart(product)}>Add to Cart</button>
              </div>
            </div>
          </div>
        ))}
      </div>
      <Modal show={modal.show} message={modal.message} onClose={closeModal} />
    </div>
  );
};

export default ProductList;
