// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import ProductList from './components/ProductList';
import CartPage from './components/CartPage';
import ProductDetails from './components/ProductDetails'; // Import ProductDetails component
import SearchBar from './components/SearchBar';
import { CartProvider } from './contexts/CartContext';
import './App.css'; // Import custom CSS

function App() {
  const [searchQuery, setSearchQuery] = useState('');
  const [sort, setSort] = useState('');

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  const handleSortChange = (e) => {
    setSort(e.target.value);
  };

  return (
    <Router>
      <CartProvider>
        <div id="root">
          <Header />
          <main className="container my-4">
            <div className="filter-sort-container">
              <div className="sort">
                <h5>Sort By</h5>
                <select value={sort} onChange={handleSortChange}>
                  <option value="">None</option>
                  <option value="priceAsc">Price: Low to High</option>
                  <option value="priceDesc">Price: High to Low</option>
                  <option value="rating">Rating</option>
                  <option value="name">Name</option>
                </select>
              </div>
            </div>
            <ConditionalSearchBar onSearch={handleSearch} />
            <Routes>
              <Route path="/" element={<ProductList searchQuery={searchQuery} sort={sort} />} />
              <Route path="/cart" element={<CartPage />} />
              <Route path="/product/:id" element={<ProductDetails />} /> {/* Add route for ProductDetails */}
            </Routes>
          </main>
          <Footer />
        </div>
      </CartProvider>
    </Router>
  );
}

const ConditionalSearchBar = ({ onSearch }) => {
  const location = useLocation();

  // Conditionally render the SearchBar based on the current path
  if (location.pathname.startsWith('/product') || location.pathname === '/cart') {
    return null;
  }

  return <SearchBar onSearch={onSearch} />;
};

export default App;
