// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import ProductList from './components/ProductList';
import CartPage from './components/CartPage';
import ProductDetails from './components/ProductDetails'; // Import ProductDetails component
import SearchBar from './components/SearchBar';
import { CartProvider } from './contexts/CartContext';
import './App.css'; // Import custom CSS

function App() {
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  return (
    <Router>
      <CartProvider>
        <div id="root">
          <Header />
          <main className="container my-4">
            <ConditionalSearchBar onSearch={handleSearch} />
            <Routes>
              <Route path="/" element={<ProductList searchQuery={searchQuery} />} />
              <Route path="/cart" element={<CartPage />} />
              <Route path="/product/:id" element={<ProductDetails />} /> {/* Add route for ProductDetails */}
            </Routes>
          </main>
          <Footer />
        </div>
      </CartProvider>
    </Router>
  );
}

const ConditionalSearchBar = ({ onSearch }) => {
  const location = useLocation();

  // Conditionally render the SearchBar based on the current path
  if (location.pathname === '/cart') {
    return null;
  }

  return <SearchBar onSearch={onSearch} />;
};

export default App;
