// src/components/CartPage.js
import React from 'react';
import { useCart } from '../contexts/CartContext';
import './CartPage.css'; // Import custom CSS for styling

const CartPage = () => {
  const { cart, removeFromCart, updateQuantity, total } = useCart();

  const handleQuantityChange = (productId, quantity) => {
    if (quantity > 0) {
      updateQuantity(productId, quantity);
    } else {
      removeFromCart(productId);
    }
  };

  return (
    <div className="cart-container">
      <h2>Your Cart</h2>
      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <div>
          <ul className="cart-list">
            {cart.map((product) => (
              <li key={product.id} className="cart-item">
                <img src={product.image} alt={product.name} className="cart-item-thumbnail" />
                <div className="cart-item-details">
                  <span className="cart-item-name">{product.name}</span>
                  <span className="cart-item-price">${product.price}</span>
                  <div className="cart-item-quantity">
                    <button className="btn-quantity" onClick={() => handleQuantityChange(product.id, product.quantity - 1)}>-</button>
                    <span className="quantity">{product.quantity}</span>
                    <button className="btn-quantity" onClick={() => handleQuantityChange(product.id, product.quantity + 1)}>+</button>
                  </div>
                  <button className="btn-remove" onClick={() => removeFromCart(product.id)}>Remove</button>
                </div>
              </li>
            ))}
          </ul>
          <h3>Total: ${total.toFixed(2)}</h3>
        </div>
      )}
    </div>
  );
};

export default CartPage;
