// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import ProductList from './components/ProductList';
import SearchBar from './components/SearchBar';
import './App.css'; // Import custom CSS

function App() {
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  return (
    <Router>
      <div id="root">
        <Header />
        <main className="container my-4">
          <ConditionalSearchBar onSearch={handleSearch} />
          <Routes>
            <Route path="/" element={<ProductList searchQuery={searchQuery} />} />
            <Route path="/cart" element={<div>Cart</div>} />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

const ConditionalSearchBar = ({ onSearch }) => {
  const location = useLocation();

  // Conditionally render the SearchBar based on the current path
  if (location.pathname === '/cart') {
    return null;
  }

  return <SearchBar onSearch={onSearch} />;
};

export default App;
