// src/components/ProductList.js
import React from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../contexts/CartContext';
import Modal from './Modal';
import './ProductList.css';

const products = [
  { id: 1, name: 'Product 1', price: 100.00, image: 'https://via.placeholder.com/200' },
  { id: 2, name: 'Product 2', price: 150.00, image: 'https://via.placeholder.com/200' },
  { id: 3, name: 'Product 3', price: 200.00, image: 'https://via.placeholder.com/200' },
  { id: 4, name: 'Product 4', price: 250.00, image: 'https://via.placeholder.com/200' },
  { id: 5, name: 'Product 5', price: 300.00, image: 'https://via.placeholder.com/200' },
  { id: 6, name: 'Product 6', price: 350.00, image: 'https://via.placeholder.com/200' },
];

const ProductList = ({ searchQuery }) => {
  const { addToCart, modal, closeModal } = useCart();

  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="row justify-content-center">
      {filteredProducts.map((product) => (
        <div className="col-md-4 mb-4 d-flex justify-content-center" key={product.id}>
          <div className="product-card">
            <img src={product.image} className="card-img-top" alt={product.name} />
            <div className="card-body">
              <h5 className="product-card-title">
                <Link to={`/product/${product.id}`} style={{ color: 'white', textDecoration: 'none' }}>{product.name}</Link>
              </h5>
              <p className="product-card-price">${product.price.toFixed(2)}</p>
              <button className="btn btn-primary" onClick={() => addToCart(product)}>Add to Cart</button>
            </div>
          </div>
        </div>
      ))}
      <Modal show={modal.show} message={modal.message} onClose={closeModal} />
    </div>
  );
};

export default ProductList;
