// src/components/ProductDetails.js
import React from 'react';
import { useParams } from 'react-router-dom';
import './ProductDetails.css'; // Import custom CSS for styling

const products = [
  { id: 1, name: 'Product 1', price: 100, description: 'This is Product 1', images: ['https://via.placeholder.com/300'], reviews: ['Great product!', 'Very satisfied'] },
  { id: 2, name: 'Product 2', price: 150, description: 'This is Product 2', images: ['https://via.placeholder.com/300'], reviews: ['Excellent!', 'Will buy again'] },
  { id: 3, name: 'Product 3', price: 200, description: 'This is Product 3', images: ['https://via.placeholder.com/300'], reviews: ['High quality!', 'Very useful'] },
  { id: 4, name: 'Product 4', price: 250, description: 'This is Product 4', images: ['https://via.placeholder.com/300'], reviews: ['Amazing!', 'Top-notch'] },
  { id: 5, name: 'Product 5', price: 300, description: 'This is Product 5', images: ['https://via.placeholder.com/300'], reviews: ['Superb!', 'Fantastic'] },
  { id: 6, name: 'Product 6', price: 350, description: 'This is Product 6', images: ['https://via.placeholder.com/300'], reviews: ['Outstanding!', 'Exceptional'] },
];

const ProductDetails = () => {
  const { id } = useParams();
  const product = products.find(p => p.id === parseInt(id));

  return (
    <div className="product-details">
      <h2>{product.name}</h2>
      <div className="product-gallery">
        <img src={product.images[0]} alt={product.name} />
      </div>
      <p>{product.description}</p>
      <h3>${product.price}</h3>
      <div className="product-reviews">
        <h4>Reviews</h4>
        <ul>
          {product.reviews.map((review, index) => (
            <li key={index}>{review}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ProductDetails;
