import React, { useContext, useEffect, useState } from 'react';
import { useCart } from '../contexts/CartContext';
import axios from 'axios';
import QRCode from 'qrcode.react';
import io from 'socket.io-client';
import '../styles/CartPage.css';

const CartPage = () => {
  const { cartItems, removeFromCart } = useCart();
  const [invoiceUrl, setInvoiceUrl] = useState('');
  const [invoicePaid, setInvoicePaid] = useState(false);
  const [total, setTotal] = useState('0.00');

  useEffect(() => {
    const totalAmount = cartItems.reduce((total, item) => {
      return total + (item.price * item.quantity);
    }, 0).toFixed(2);
    setTotal(totalAmount);
  }, [cartItems]);

  const handleCheckout = async () => {
    try {
      const response = await axios.post('http://localhost:5000/create-invoice', {
        price: total,
        currency: 'USD',
      });
      console.log('Invoice creation response:', response.data);
      setInvoiceUrl(response.data.paymentLink);
      console.log('Invoice URL:', response.data.paymentLink);
    } catch (error) {
      console.error('Error creating invoice:', error);
    }
  };

  const handleInvoicePaid = (invoiceId) => {
    setInvoicePaid(true);
    // Additional logic to handle order confirmation and showing orderId
  };

  useEffect(() => {
    const socket = io('http://localhost:5000');
    socket.on('paymentConfirmed', ({ invoiceId }) => {
      handleInvoicePaid(invoiceId);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  return (
    <div className="cart-page">
      <h2>Cart</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <ul>
          {cartItems.map((item, index) => (
            <li key={index}>
              {item.name} - ${item.price} x {item.quantity}
              <button onClick={() => removeFromCart(item.id)}>Remove</button>
            </li>
          ))}
        </ul>
      )}
      <h3>Total: ${total}</h3>
      {!invoiceUrl && !invoicePaid && (
        <button onClick={handleCheckout} className="btn-primary">Proceed to Checkout</button>
      )}
      {invoiceUrl && !invoicePaid && (
        <>
          <p>Invoice created! Pay using the link below:</p>
          <a href={invoiceUrl} target="_blank" rel="noopener noreferrer">{invoiceUrl}</a>
          <p>Or scan the QR code below:</p>
          <QRCode value={invoiceUrl} />
        </>
      )}
      {invoicePaid && (
        <>
          <p>Invoice paid successfully!</p>
          <p>Your order number is: [Order ID]</p>
        </>
      )}
    </div>
  );
};

export default CartPage;
