import React, { useContext, useState, useEffect } from 'react';
import { CartContext } from '../contexts/CartContext';
import axios from 'axios';
import io from 'socket.io-client';

const socket = io('http://localhost:5000');

const CheckoutPage = () => {
  const { cartItems, calculateTotal } = useContext(CartContext);
  const [invoiceUrl, setInvoiceUrl] = useState('');
  const [paymentConfirmed, setPaymentConfirmed] = useState(false);
  const [orderId, setOrderId] = useState('');

  const handleCheckout = async () => {
    try {
      const response = await axios.post('http://localhost:5000/create-invoice', {
        price: calculateTotal(),
        currency: 'USD',
      });
      setInvoiceUrl(response.data.paymentLink);
    } catch (error) {
      console.error('Error creating invoice:', error);
    }
  };

  useEffect(() => {
    socket.on('paymentConfirmed', (data) => {
      setPaymentConfirmed(true);
      setOrderId(data.invoiceId);
    });

    return () => {
      socket.off('paymentConfirmed');
    };
  }, []);

  return (
    <div>
      <h1>Checkout Page</h1>
      {paymentConfirmed ? (
        <div>
          <h2>Payment Confirmed</h2>
          <p>Your order ID is: {orderId}</p>
        </div>
      ) : (
        <div>
          {invoiceUrl ? (
            <div>
              <p>Invoice created! Pay using the link below:</p>
              <a href={invoiceUrl} target="_blank" rel="noopener noreferrer">
                {invoiceUrl}
              </a>
            </div>
          ) : (
            <div>
              <button onClick={handleCheckout}>Proceed to Checkout</button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default CheckoutPage;
