// src/components/Checkout.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

const Checkout = () => {
  const [invoiceUrl, setInvoiceUrl] = useState('');
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const total = queryParams.get('total');

  const handleCheckout = async () => {
    try {
      const response = await axios.post('http://localhost:5000/create-invoice', {
        price: total,
        currency: 'USD', // or the currency you want to use
      });
      setInvoiceUrl(response.data.checkoutLink);
    } catch (error) {
      console.error('Error creating invoice:', error);
    }
  };

  useEffect(() => {
    handleCheckout();
  }, [total]);

  return (
    <div>
      <h2>Checkout</h2>
      {invoiceUrl ? (
        <div>
          <p>Invoice created! Pay using the link below:</p>
          <a href={invoiceUrl} target="_blank" rel="noopener noreferrer">
            {invoiceUrl}
          </a>
        </div>
      ) : (
        <p>Creating invoice...</p>
      )}
    </div>
  );
};

export default Checkout;
