import React, { useEffect, useState } from 'react';
import { useCart } from '../contexts/CartContext';
import axios from 'axios';
import QRCode from 'qrcode.react';
import io from 'socket.io-client';
import '../styles/CartPage.css';


const CartPage = () => {
  const { cartItems, removeFromCart } = useCart();
  const [invoiceUrl, setInvoiceUrl] = useState('');
  const [invoicePaid, setInvoicePaid] = useState(false);
  const [orderId, setOrderId] = useState(null);

  const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0).toFixed(2);

  const handleCheckout = async () => {
    try {
      const res = await axios.post('http://localhost:5000/create-invoice', {
        price: total,
        currency: 'USD',
      });
      setInvoiceUrl(res.data.paymentLink);
      setOrderId(res.data.invoiceId);
    } catch (err) {
      console.error('Error creating invoice:', err);
    }
  };

  useEffect(() => {
    const socket = io('http://localhost:5000'); // adjust if using ngrok or production

    socket.on('paymentConfirmed', ({ invoiceId }) => {
      console.log('Invoice settled webhook received:', invoiceId);
      if (invoiceId === orderId) {
        setInvoicePaid(true);
      }
    });

    return () => {
      socket.disconnect();
    };
  }, [orderId]);

  console.log('Expected orderId:', orderId);
  console.log('Received invoiceId from webhook:', invoiceId);


  if (invoicePaid) {
    return (
      <div className="checkout-confirmation">
        <p>Invoice paid successfully!</p>
        <p>Your order number is: {orderId}</p>
        <a href="/" className="link-button">Back to Home</a>
      </div>
    );
  }

  return (
    <div className="cart-page">
      <h2>Shopping Cart</h2>
      {cartItems.map(item => (
        <div className="cart-item" key={item.id}>
          <img src={item.image} alt={item.name} />
          <div>
            <h3>{item.name}</h3>
            <p>${item.price.toFixed(2)}</p>
            <p>Quantity: {item.quantity}</p>
          </div>
          <button onClick={() => removeFromCart(item.id)}>Remove</button>
        </div>
      ))}

      <h3>Total: ${total}</h3>

      {invoiceUrl ? (
        <div className="invoice-section">
          <p>Invoice created! Pay using the link below:</p>
          <a href={invoiceUrl} target="_blank" rel="noreferrer">{invoiceUrl}</a>
          <p>Or scan the QR code below:</p>
          <QRCode value={invoiceUrl} />
        </div>
      ) : (
        <button onClick={handleCheckout}>Proceed to Checkout</button>
      )}
    </div>
  );
};

export default CartPage;
