// src/components/ProductDetails.js
import React from 'react';
import { useParams } from 'react-router-dom';
import { useCart } from '../contexts/CartContext';
import './ProductDetails.css';

const products = [
  { id: 1, name: 'Product 1', price: 100.00, description: 'Description of product 1', image: 'https://via.placeholder.com/250' },
  { id: 2, name: 'Product 2', price: 150.00, description: 'Description of product 2', image: 'https://via.placeholder.com/250' },
  { id: 3, name: 'Product 3', price: 200.00, description: 'Description of product 3', image: 'https://via.placeholder.com/250' },
  { id: 4, name: 'Product 4', price: 250.00, description: 'Description of product 4', image: 'https://via.placeholder.com/250' },
  { id: 5, name: 'Product 5', price: 300.00, description: 'Description of product 5', image: 'https://via.placeholder.com/250' },
  { id: 6, name: 'Product 6', price: 350.00, description: 'Description of product 6', image: 'https://via.placeholder.com/250' },
];

const ProductDetails = () => {
  const { id } = useParams();
  const { addToCart } = useCart();
  const product = products.find((product) => product.id === parseInt(id));

  if (!product) {
    return <div>Product not found</div>;
  }

  return (
    <div className="product-details">
      <h2>{product.name}</h2>
      <img src={product.image} alt={product.name} />
      <p>{product.description}</p>
      <p>Price: ${product.price.toFixed(2)}</p>
      <button className="btn btn-primary" onClick={() => addToCart(product)}>Add to Cart</button>
    </div>
  );
};

export default ProductDetails;
