import React, { useContext, useState, useEffect } from 'react';
import { useCart } from '../contexts/CartContext';
import axios from 'axios';
import '../styles/CheckoutPage.css';
import QRCode from 'qrcode.react';

const CheckoutPage = () => {
  const { cartItems, total } = useCart();
  const [checkoutLink, setCheckoutLink] = useState(null);
  const [paymentLink, setPaymentLink] = useState(null);
  const [paymentLinkQr, setPaymentLinkQr] = useState(null);
  const [paymentConfirmed, setPaymentConfirmed] = useState(false);

  const handleCheckout = async () => {
    try {
      const response = await axios.post('http://localhost:5000/create-invoice', {
        price: total.toFixed(2),
        currency: 'USD',
      });
      setCheckoutLink(response.data.checkoutLink);
      setPaymentLink(response.data.paymentLink);
      setPaymentLinkQr(response.data.paymentLinkQr);
    } catch (error) {
      console.error('Error creating invoice:', error);
    }
  };

  useEffect(() => {
    const socket = new WebSocket('ws://localhost:5000');
    socket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.event === 'paymentConfirmed') {
        setPaymentConfirmed(true);
      }
    };

    return () => {
      socket.close();
    };
  }, []);

  return (
    <div className="checkout-container">
      <h1>Checkout</h1>
      <div className="cart-items">
        {cartItems.map((item) => (
          <div key={item.id} className="cart-item">
            <img src={item.image} alt={item.name} />
            <div>
              <h3>{item.name}</h3>
              <p>{item.price}</p>
            </div>
          </div>
        ))}
      </div>
      <div className="total">
        <h2>Total: ${total.toFixed(2)}</h2>
      </div>
      <button className="btn btn-primary" onClick={handleCheckout}>
        Proceed to Checkout
      </button>
      {checkoutLink && !paymentConfirmed && (
        <div className="payment-link">
          <p>Invoice created! Pay using the link below:</p>
          <a href={checkoutLink} target="_blank" rel="noopener noreferrer">
            {checkoutLink}
          </a>
          <div>
            <p>Or scan the QR code below:</p>
            <QRCode value={paymentLinkQr} />
          </div>
        </div>
      )}
      {paymentConfirmed && (
        <div className="payment-confirmed">
          <h3>Payment confirmed! Thank you for your purchase.</h3>
        </div>
      )}
    </div>
  );
};

export default CheckoutPage;
