import React, { createContext, useContext, useReducer } from 'react';

const CartContext = createContext();

const initialState = {
  cartItems: [],
  total: 0,
};

const cartReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_TO_CART':
      const updatedCartItems = [...state.cartItems, action.payload];
      const updatedTotal = updatedCartItems.reduce((acc, item) => acc + item.price, 0);
      return {
        ...state,
        cartItems: updatedCartItems,
        total: updatedTotal,
      };
    case 'REMOVE_FROM_CART':
      const filteredCartItems = state.cartItems.filter(item => item.id !== action.payload.id);
      const newTotal = filteredCartItems.reduce((acc, item) => acc + item.price, 0);
      return {
        ...state,
        cartItems: filteredCartItems,
        total: newTotal,
      };
    default:
      return state;
  }
};

export const CartProvider = ({ children }) => {
  const [state, dispatch] = useReducer(cartReducer, initialState);

  return (
    <CartContext.Provider value={{ cartItems: state.cartItems, total: state.total, dispatch }}>
      {children}
    </CartContext.Provider>
  );
};

export const useCart = () => {
  return useContext(CartContext);
};
